								  val X = payloadArg(0)
												val Y = payloadArg(1)    
								solve("updateWaiterLoc(_,$X,$Y)","") //set resVar	
						}
						solve("updateWaiterState(X,cancelplan)","") //set resVar	
						solve("roomstate(S)","") //set resVar	
						if( currentSolution.isSuccess() ) {updateResourceRep( getCurSol("S").toString()  
						)
						}
						else
						{}
						if( Debug 
						 ){println("end of handlePlanCancel")
						 readLine()  
						}
					}
					 transition( edgeName="goto",targetState="waitState", cond=doswitch() )
				}	 
				state("cleanTable") { //this:State
					action { //it:State
						if(Debug) 
						println("waiter cleanTable")
						if( checkMsgContent( Term.createTerm("moveOk(X,Y)"), Term.createTerm("moveOk(X,Y)"), 
						                        currentMsg.msgContent()) ) { //set msgArgList
								  val X = payloadArg(0)
												val Y = payloadArg(1)    
								solve("updateWaiterLoc(teatable(Num),$X,$Y)","") //set resVar	
								 val TableNum = getCurSol("Num").toString()  
								solve("updateWaiterState(X,atteatable($TableNum))","") //set resVar	
								solve("roomstate(S)","") //set resVar	
								if( currentSolution.isSuccess() ) {updateResourceRep( getCurSol("S").toString()  
								)
								}
								else
								{}
						}
						stateTimer = TimerActor("timer_cleanTable", 
							scope, context!!, "local_tout_waiter_cleanTable", 100.toLong() )
					}
					 transition(edgeName="t046",targetState="doClean",cond=whenTimeout("local_tout_waiter_cleanTable"))   
					transition(edgeName="t047",targetState="checkTableAvail",cond=whenRequest("table"))
					transition(edgeName="t048",targetState="reach",cond=whenDispatch("clientatentrance"))
					transition(edgeName="t049",targetState="takeOrder",cond=whenDispatch("placeorder"))
					transition(edgeName="t050",targetState="serveOrder",cond=whenReply("orderready"))
					transition(edgeName="t051",targetState="getPayment",cond=whenDispatch("payment"))
				}	 
				state("doClean") { //this:State
					action { //it:State
						if(Debug) 
						println("waiter doClean")
						solve("updateWaiterState(atteatable(TableNum),cleaning(TableNum))","") //set resVar	
						 val TableNum = getCurSol("TableNum").toString()  
						delay(DelayTime)
						solve("cleanTable($TableNum)","") //set resVar	
						solve("updateWaiterState(X,cleaned($TableNum))","") //set resVar	
						solve("roomstate(S)","") //set resVar	
						if( currentSolution.isSuccess() ) {updateResourceRep( getCurSol("S").toString()  
						)
						}
						else
						{}
					}
					 transition( edgeName="goto",targetState="checkClientOnHold", cond=doswitch() )
				}	 
				state("checkClientOnHold") { //this:State
					action { //it:State
						if(Debug) 
						println("table cleaned letting somebody in")
						 if(Debug)  
						solve("clients(L)","") //set resVar	
						 if(Debug) println("clients are: ${getCurSol("L").toString()}")  
						solve("client(Cid,onhold)","") //set resVar	
						if( currentSolution.isSuccess() ) { val OnHoldCid = getCurSol("Cid").toString()  
						 if(Debug)println("ok, client waiting is num: $OnHoldCid")  
						if(  Debug  
						 ){solve("roomstate(S)","") //set resVar	
						if( currentSolution.isSuccess() ) { println("------------------------------------pre solves ${getCurSol("S").toString()}")  
						}
						else
						{}
						}
						solve("retract(client($OnHoldCid,onhold))","") //set resVar	
						solve("reserveTable(N,$OnHoldCid)","") //set resVar	
						if( currentSolution.isSuccess() ) {solve("roomstate(S)","") //set resVar	
						if( currentSolution.isSuccess() ) {updateResourceRep( getCurSol("S").toString()  
						)
						}
						else
						{}
						if(Debug) println("DEBUG ---------- sending clientatantrence($OnHoldCid)  ------------")  
						forward("clientatentrance", "clientatentrance($OnHoldCid)" ,"waiter" ) 
						}
						else
						{}
						if(  Debug  
						 ){solve("roomstate(S)","") //set resVar	
						if( currentSolution.isSuccess() ) { println("------------------------------------post solves ${getCurSol("S").toString()}")  
						}
						else
						{}
						}
						solve("teatable(Num,free,dirty)","") //set resVar	
						if( currentSolution.isSuccess() ) { val TableNum = getCurSol("Num").toString()  
						forward("cleantable", "cleantable($TableNum)" ,"waiter" ) 
						}
						else
						{}
						}
						else
						{}
					}
					 transition( edgeName="goto",targetState="waitState", cond=doswitch() )
				}	 
				state("maybeRest") { //this:State
					action { //it:State
					}
					 transition( edgeName="goto",targetState="waitState", cond=doswitchGuarded({ IsWaiterAtHome  
					}) )
					transition( edgeName="goto",targetState="checkDirtyTable", cond=doswitchGuarded({! ( IsWaiterAtHome  
					) }) )
				}	 
				state("checkDirtyTable") { //this:State
					action { //it:State
						solve("teatable(Num,free,dirty)","") //set resVar	
						if( currentSolution.isSuccess() ) { val TableNum = getCurSol("Num").toString()  
						forward("cleantable", "cleantable($TableNum)" ,"waiter" ) 
						}
						else
						{}
						stateTimer = TimerActor("timer_checkDirtyTable", 
							scope, context!!, "local_tout_waiter_checkDirtyTable", RestWaitTime )
					}
					 transition(edgeName="t052",targetState="rest",cond=whenTimeout("local_tout_waiter_checkDirtyTable"))   
					transition(edgeName="t053",targetState="thinkCleanTable",cond=whenDispatch("cleantable"))
					transition(edgeName="t054",targetState="reach",cond=whenDispatch("clientatentrance"))
					transition(edgeName="t055",targetState="takeOrder",cond=whenDispatch("placeorder"))
					transition(edgeName="t056",targetState="serveOrder",cond=whenReply("orderready"))
					transition(edgeName="t057",targetState="getPayment",cond=whenDispatch("payment"))
				}	 
				state("rest") { //this:State
					action { //it:State
						if(Debug) 
						println("waiter rest")
						if( checkMsgContent( Term.createTerm("moveKo(X,Y)"), Term.createTerm("moveKo(X,Y)"), 
						                        currentMsg.msgContent()) ) { //set msgArgList
								  val X = payloadArg(0)
												val Y = payloadArg(1)    
								solve("updateWaiterLoc(_,$X,$Y)","") //set resVar	
								solve("roomstate(S)","") //set resVar	
								if( currentSolution.isSuccess() ) {updateResourceRep( getCurSol("S").toString()  
								)
								}
								else
								{}
						}
						solve("coordinatesTo(home,OldX,OldY,X,Y)","") //set resVar	
						if( currentSolution.isSuccess() ) {  val OldX = getCurSol("OldX").toString()
										val OldY = getCurSol("OldY").toString()
										val X = getCurSol("X").toString()
										val Y = getCurSol("Y").toString()		 
						request("moveTo", "moveTo($OldX,$OldY,$X,$Y)" ,"robot" )  
						}
						else
						{}
					}
					 transition(edgeName="t058",targetState="atHome",cond=whenReply("moveOk"))
					transition(edgeName="t059",targetState="rest",cond=whenReply("moveKo"))
					transition(edgeName="t060",targetState="cancelCurrentPlan",cond=whenRequest("table"))
					transition(edgeName="t061",targetState="cancelCurrentPlan",cond=whenDispatch("clientatentrance"))
					transition(edgeName="t062",targetState="cancelCurrentPlan",cond=whenDispatch("placeorder"))
					transition(edgeName="t063",targetState="cancelCurrentPlan",cond=whenReply("orderready"))
					transition(edgeName="t064",targetState="cancelCurrentPlan",cond=whenDispatch("payment"))
					transition(edgeName="t065",targetState="thinkCleanTable",cond=whenDispatch("cleantable"))
				}	 
				state("atHome") { //this:State
					action { //it:State
						if(Debug) 
						println("waiter athome")
						solve("updateWaiterState(X,rest)","") //set resVar	
						solve("updateWaiterLoc(home,X,Y)","") //set resVar	
						solve("roomstate(S)","") //set resVar	
						if( currentSolution.isSuccess() ) {updateResourceRep( getCurSol("S").toString()  
						)
						}
						else
						{}
					}
					 transition( edgeName="goto",targetState="waitState", cond=doswitch() )
				}	 
			}
		}
}
